mapper.update:
update tbl_approval
set status=#{status},title=#{title}, content=#{content},curr_approval=#{curr_approval},next_approval=#{next_approval}, regdate=sysdate

	,filename=#{filename}

where no=#{no}

mapper.select:
select no, status, writer_name, receiver_dname, title,regdate
from tbl_approval


	where next_approval=#{vo.next_approval}
	



	
		where writer=#{vo.writer}
						
	
  


	where status=#{vo.status}					
	and (receiver=#{vo.receiver} or receiver=0)
	

order by regdate desc

select no, status, writer_name,	receiver_dname, title,regdate
from tbl_approval
where receiver=#{deptno}
and status!=#{status}

order by regdate desc

select count(*) from tbl_approval

	where next_approval=#{vo.next_approval}
	



	
		where writer=#{vo.writer}
						
	
  


	where status=#{vo.status}					
	and (receiver=#{vo.receiver} or receiver=0)

select count(*)
from tbl_approval
where receiver=#{deptno}
and status!=#{status}

mapper.update:
update tbl_approval
set curr_approval=#{curr_approval}, next_approval=#{next_approval}
where no=#{no}

update tbl_approval
set status=#{status}
where no=#{no}
